# coding:utf-8
import xlwt

head = ['姓名', '年龄', '学号', '年级']
data = [
        ['小明', 10.0, 19910.0, '三年级'],
        ['小红', 12.0, 19911.0, '三年级'],
        ['小刚', 11.0, 19912.0, '五年级'],
        ['小月', 11.0, 19913.0, '四年级'],
        ['小黑', 9.0, 19914.0, '二年级']
]

# 1.创建一个工作簿
work_book = xlwt.Workbook(encoding='utf-8')
# 2.在工作簿中添加一个sheet
sheet = work_book.add_sheet(sheetname='学生信息')
# 3.写表头
for i in range (len(head)):
    sheet.write(0,i,head[i])
# 4.写内容
for i in range(len(data)):
    for j in range(len(head)):
        sheet.write(i+1,j,data[i][j])
# 5.保存
work_book.save('学生信息2.xlsx')


